var env = {
    host : process.env.VCAP_APP_HOST || 'localhost',
    port : process.env.VCAP_APP_PORT || 80,
    isCloud : process.env.VCAP_APP_HOST ? true : false,

    appHostName : 'letsync.cloudfoundry.com',
    clientId : '',
    clientSecret : ''


};

if (process.env.VCAP_SERVICES) {
    var vcapServices = JSON.parse(process.env.VCAP_SERVICES);
    var cfRedis = vcapServices['redis-2.2'][0];
    env.redisHost = cfRedis.credentials.hostname;
    env.redisPort = cfRedis.credentials.port;
    env.redisPassword = cfRedis.credentials.password;
}

console.log(env);
module.exports = env;